import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';

function handleClick() {
  console.log('clicou!');
}

function secondClick() {
  console.log('clicou de novo!');
}

function thirdClick() {
  console.log('clicou mais uma vez!');
}

class App extends Component {
  render () {
    return (
      <div>
        <button onClick={handleClick}>Aperte!</button>
        <button onClick={secondClick}>Aperte de novo!</button>
        <button onClick={thirdClick}>Aperte mais uma vez!</button>
      </div>
    );
  }
}

export default App;
