import logo from './logo.svg';
import './App.css';
import React, { Component } from 'react';

class App extends Component {
  constructor () {
    super()

    this.handleClick = this.handleClick.bind(this);
    this.secondClick = this.secondClick.bind(this);
    this.thirdClick = this.thirdClick.bind(this);
  }

  handleClick() {
    console.log('clicou!');
  }

  secondClick() {
    console.log('clicou de novo!');
  }

  thirdClick() {
    console.log('clicou mais uma vez!');
  }

  render () {
    return (
      <div>
        <button onClick={handleClick}>Aperte!</button>
        <button onClick={secondClick}>Aperte de novo!</button>
        <button onClick={thirdClick}>Aperte mais uma vez!</button>
      </div>
    );
  }
}

export default App;
